// Get the HTML content from the current item
const html = $json.data;
const websiteUrl = $json.website_url;

// Simple HTML parsing without external libraries
function extractBetween(str, start, end) {
  const startIndex = str.toLowerCase().indexOf(start.toLowerCase());
  if (startIndex === -1) return '';
  const endIndex = str.toLowerCase().indexOf(end.toLowerCase(), startIndex + start.length);
  if (endIndex === -1) return '';
  return str.substring(startIndex + start.length, endIndex).trim();
}

function countOccurrences(str, substring) {
  return (str.toLowerCase().match(new RegExp(substring.toLowerCase(), 'g')) || []).length;
}

// Extract basic information
const title = extractBetween(html, '<title>', '</title>') || 'No title found';

// Extract meta description
const descriptionMatch = html.toLowerCase().match(/<meta\s+name=['"]description['"][^>]*content=['"]([^'"]*)['"]/);
const description = descriptionMatch ? descriptionMatch[1] : '';

// Check for modern features
const hasResponsiveDesign = html.toLowerCase().includes('viewport') && html.toLowerCase().includes('width=device-width');
const hasSSL = websiteUrl && typeof websiteUrl === 'string' ? websiteUrl.startsWith('https://') : false;
const hasAnalytics = html.toLowerCase().includes('google-analytics') || html.toLowerCase().includes('gtag') || html.toLowerCase().includes('ga(');
const hasLiveChat = html.toLowerCase().includes('chat') || html.toLowerCase().includes('messenger') || html.toLowerCase().includes('intercom');
const hasSEO = html.toLowerCase().includes('og:title') || html.toLowerCase().includes('twitter:title');
const hasContactForm = html.toLowerCase().includes('<form') && (html.toLowerCase().includes('contact') || html.toLowerCase().includes('email'));

// Performance indicators
const imageCount = countOccurrences(html, '<img');
const scriptCount = countOccurrences(html, '<script');
const cssCount = countOccurrences(html, '<link') + countOccurrences(html, '<style');

// Check for modern frameworks/libraries
const hasJQuery = html.toLowerCase().includes('jquery');
const hasBootstrap = html.toLowerCase().includes('bootstrap');
const hasReact = html.toLowerCase().includes('react') || html.toLowerCase().includes('_react');
const hasVue = html.toLowerCase().includes('vue.js') || html.toLowerCase().includes('vue.min.js');

// Extract some headings
const h1Matches = html.match(/<h1[^>]*>(.*?)<\/h1>/gi) || [];
const headings = h1Matches.slice(0, 3).map(h => h.replace(/<[^>]*>/g, '').trim());

// Check for accessibility features
const hasAltTags = html.toLowerCase().includes('alt=');
const hasAriaLabels = html.toLowerCase().includes('aria-label');

// Check for social media links
const hasFacebook = html.toLowerCase().includes('facebook.com');
const hasTwitter = html.toLowerCase().includes('twitter.com') || html.toLowerCase().includes('x.com');
const hasLinkedIn = html.toLowerCase().includes('linkedin.com');
const hasInstagram = html.toLowerCase().includes('instagram.com');

return {
  website_info: {
    title: title,
    description: description,
    headings: headings,
    url: websiteUrl,
    features: {
      hasResponsiveDesign: hasResponsiveDesign,
      hasSSL: hasSSL,
      hasAnalytics: hasAnalytics,
      hasLiveChat: hasLiveChat,
      hasSEO: hasSEO,
      hasContactForm: hasContactForm,
      hasAltTags: hasAltTags,
      hasAriaLabels: hasAriaLabels
    },
    technology: {
      hasJQuery: hasJQuery,
      hasBootstrap: hasBootstrap,
      hasReact: hasReact,
      hasVue: hasVue
    },
    performance: {
      imageCount: imageCount,
      scriptCount: scriptCount,
      cssCount: cssCount
    },
    social: {
      hasFacebook: hasFacebook,
      hasTwitter: hasTwitter,
      hasLinkedIn: hasLinkedIn,
      hasInstagram: hasInstagram
    }
  }
};

const websiteInfo = $json.website_info;
const recommendations = [];
const benefits = [];
const issues = [];

// Analyze and generate recommendations
if (!websiteInfo.features.hasResponsiveDesign) {
  recommendations.push("Implement mobile-responsive design");
  benefits.push("Increase mobile traffic by 40-60%");
  issues.push("Not mobile-friendly");
}

if (!websiteInfo.features.hasSSL) {
  recommendations.push("Add SSL certificate for security");
  benefits.push("Improve SEO ranking and user trust");
  issues.push("No SSL security");
}

if (!websiteInfo.features.hasAnalytics) {
  recommendations.push("Install Google Analytics for tracking");
  benefits.push("Track visitor behavior and optimize conversions");
  issues.push("No analytics tracking");
}

if (!websiteInfo.features.hasSEO) {
  recommendations.push("Optimize SEO with meta tags and structured data");
  benefits.push("Increase organic search traffic by 25-50%");
  issues.push("Poor SEO optimization");
}

if (websiteInfo.performance.imageCount > 15) {
  recommendations.push("Optimize images for faster loading");
  benefits.push("Reduce page load time by 30-50%");
  issues.push("Too many unoptimized images");
}

if (!websiteInfo.features.hasLiveChat) {
  recommendations.push("Add live chat for customer support");
  benefits.push("Increase conversion rates by 20-30%");
  issues.push("No live chat support");
}

if (!websiteInfo.features.hasContactForm) {
  recommendations.push("Add professional contact forms");
  benefits.push("Improve lead generation and user engagement");
  issues.push("No contact forms");
}

// Always suggest modern improvements
recommendations.push("Modern UI/UX design with contemporary aesthetics");
benefits.push("Reduce bounce rate and increase user engagement");

recommendations.push("Content Management System for easy updates");
benefits.push("Save time and reduce maintenance costs");

// Calculate lead score
let leadScore = 0;
if (!websiteInfo.features.hasResponsiveDesign) leadScore += 25;
if (!websiteInfo.features.hasSSL) leadScore += 20;
if (!websiteInfo.features.hasAnalytics) leadScore += 15;
if (!websiteInfo.features.hasSEO) leadScore += 20;
if (!websiteInfo.features.hasLiveChat) leadScore += 10;
if (websiteInfo.performance.imageCount > 15) leadScore += 10;

return {
  recommendations: recommendations,
  benefits: benefits,
  issues: issues,
  priority_issues: issues.slice(0, 3),
  lead_score: leadScore,
  total_recommendations: recommendations.length
};

